{
  #FILTER: Fetch Real-time (Active) Queued Tasks on the System - using filters.
  task(
    filter: {
      #The main filter for active tasks is isActive: true
      and: [
        { channelType: { equals: telephony } }
        # { status: { equals: "parked" } }
        { direction: { equals: "inbound" } }
        { isActive: { equals: true } }
      ]
    }
  ) {
    tasks {
      #Add more fields here as needed
      id
      origin
      destination
      isActive
      direction
      owner {
        id
        name
      }
      lastEntryPoint {
        id
        name
      }
    }
  }
}

{
  #AGGREGATES/FORMULAS: Usage of filters, aggregates, pagination and custom fields
  task(
    timeComparator: endedTime #When filtering tasks, use the end time of the task
    filter: {
      #Filter the type of Task
      and: [
        { channelType: { equals: telephony } } #Telephony calls only
         { status: { equals: "ended" } } #Final Disposition
        # { direction: { equals: "inbound" } } #Inbound call only
        { isActive: { equals: false } } #Resolved call only
      ]
    }
    aggregation: {
      #Define custom formulas and Aggregations - NOTE that the FIELDS you insert below has an effect on this.
      aggr: [
        { field: totalDuration, type: sum, name: "Total Duration" }
        {
          field: connectedDuration
          type: sum
          name: "Average Connected Duration"
        }
      ]
    }
    pagination: { cursor: "0" } #Show first page
  ) {
    tasks {
      #Task Metadata - Note that since these are aggregates, you need to use the GROUPBY Fields HERE
      origin
      destination
      lastWrapupCodeName #Why the customer called
      #Aggregation Results - Print Total and Average Durations
      aggregation {
        name
        value
      }
    }
    #Pagination Information
    pageInfo {
      endCursor
      hasNextPage
    }
  }
}

{
  #BASIC: Basic query to request for certain fields
  task(
    #From and To timestamps in Epoch Time
    from: 1635750000000
    to: 1638345599000
    #Start with Page 0
    pagination: { cursor: "0" }
  ) {
    tasks {
      createdTime #ContactStartTime
      channelType #ChannelType
      entryPoint {
        name #EntrypointName
      }
      lastQueue {
        name #NameOfQueue
      }
      direction
      terminationType #HandleType
      owner {
        name #AgentName
      }
      id #ContactSessionId
      status #CurrentState
      destination #DNIS
      isActive #WhetherRealtimeOrHistorical
      selfserviceDuration #IVR
      ringingDuration #Ring
      queueDuration #QueueTime
      connectedDuration #HandleTime
      totalDuration #QueueTime
      holdDuration #HoldDuration
      wrapupDuration #WrapUp
    }
    #Pagination Information
    pageInfo {
      endCursor
      hasNextPage
    }
  }
}

{
  #FILTER: Get the Task details using CallBack filters and logical operators to match a condition
  task(
    #From and To timestamps in Epoch Time
    from: 1646121600000
    # to: 1650009600
    #Add a filter that checks if a Callback was made and with a status
    filter: {
      and: [
        { callbackData: { equals: { callbackNumber: "nnnnnnnnnnn" } } }
        { callbackData: { equals: { callbackStatus: "Success" } } }
      ]
    }
  ) {
    tasks {
      callbackData {
        callbackRequestTime
        callbackConnectTime
        callbackNumber
        callbackStatus
        callbackOrigin
        callbackType
      }
    }
  }
}

{
  #CurrentInteraction: Usage of filters, aggregates, pagination and custom fields to find out information about the current interaction
  task(
    #Check Interaction Details based on a Specific Interaction ID and get other information about this call
    from: 1646699261000 #Set this to dateTime NOW so you're looking for a task active right now
    timeComparator: createdTime #Filter by created Time only
    filter: {
      #Filter the type of Task
      and: [
        #Telephony call
        # { id: { equals: "85f0c70c-19d6-4392-ad04-1854bb757044" } }
        # Optional filters - you can remove these
        { channelType: { equals: telephony } } #Telephony call
        { origin: { equals: "+12345670000" } } #Customer ANI
         { destination: { equals: "+14563454567" } } #DNIS
        # { direction: { equals: "inbound" } } #Inbound call only
      ]
    }
  ) {
    tasks {
      #Task Metadata
      id
      isActive
      createdTime
      endedTime
      origin
      destination
      totalDuration
      #Treatment Details
      lastEntryPoint {
        #Entrypoint Details
        id
        name
      }
      lastQueue {
        #Queue Details
        id
        name
      }
      lastTeam {
        #Team Details
        id
        name
      }
      owner {
        #Agent Details
        name
        id
      }
    }
  }
}

{
  #SIMPLE: Simply retrieve all task IDs

  task(
    #Search all tasks in a 15 day window.
    from: 1646095690000
    to: 1647305290000
  ) {
    tasks {
      #Return the ID of the task
      id
      #You can add more fields below
    }
  }
}

{
  #LAST AGENT INTERACTIONS: Usage of filters, aggregates, pagination and custom fields to find when the customer called last in a 7 day window and who they reached
  task(
    #Check if Customer called in the last 7 days and who they were connected to
    from: 1646699261000
    to: 1647304061000
    timeComparator: createdTime #Filter by created Time only
    filter: {
      #Filter the type of Task
      and: [
        { channelType: { equals: telephony } } #Telephony calls only
        { origin: { equals: "+12345677890" } } #Customer ANI
        { status: { equals: "ended" } } #Final Disposition
        { direction: { equals: "inbound" } } #Inbound call only
        { isActive: { equals: false } } #Resolved call only
      ]
    }
    pagination: { cursor: "0" } #Display first page only
  ) {
    tasks {
      #Task Metadata
      id
      createdTime
      endedTime
      origin
      destination
      lastWrapupCodeName #Why the customer called
      totalDuration
      selfserviceDuration
      captureRequested
      #Treatment Details
      lastEntryPoint {
        #Entrypoint Details
        id
        name
      }
      lastQueue {
        #Queue Details
        id
        name
      }
      lastTeam {
        #Team Details
        id
        name
      }
      owner {
        #Agent Details
        name
        id
      }
    }
    #Pagination Information
    pageInfo {
      endCursor
      hasNextPage
    }
  }
}

