  agentSession{
	agentSessions{
	agentName
  }
  }

}

{
  #SIMPLE: Get channel Type
  taskDetails{
      tasks{
          channelType
      }
  }



}

{
  #TOTAL CALLS BY Entry Point

  task(
    from: 1653952506445 #This can be set to Date.now() - (days * 24 * 60 * 60 * 1000) for lookback in days
    to: 1654557391185 #This can be set to Date.now() in millis
    timeComparator: createdTime
    filter: {
      and: [
        { direction: { equals: "inbound" } }
        { channelType: { equals: telephony } }
      ]
    }
    aggregations: {
      field: "id"
      type: count
      name: "Total Contacts by Entry Point"
    }
  ) {
    tasks {
      lastEntryPoint {
        name
        id
      }
      aggregation {
        name
        value
      }
    }
    pageInfo {
      hasNextPage
      endCursor
    }
  }
}

{
  #TOTAL CALLS Realtime by Queued Calls

  task(
    from: 1654557726452 #This can be set to Date.now() - (days * 24 * 60 * 60 * 1000) for lookback in days
    timeComparator: createdTime
    filter: {
      and: [
        { direction: { equals: "inbound" } }
        { isActive: { equals: true } }
        { status: { equals: "parked" } }
        { channelType: { equals: telephony } }
      ]
    }
    aggregations: {
      field: "id"
      type: count
      name: "Total Queued Contacts RealTime"
    }
  ) {
    tasks {
      aggregation {
        name
        value
      }
    }
    pageInfo {
      hasNextPage
      endCursor
    }
  }
}

{
  #TOTAL CALLS BY Queue and Average Handle Time by Queue

  task(
    from: 1653952506445 #This can be set to Date.now() - (days * 24 * 60 * 60 * 1000) for lookback in days
    to: 1654557391185 #This can be set to Date.now() in millis
    timeComparator: createdTime
    filter: {
      and: [
        { direction: { equals: "inbound" } }
        { channelType: { equals: telephony } }
      ]
    }
    aggregations: [
      { field: "id", type: count, name: "Total Contacts by Queue" }
      { field: "queueDuration", type: average, name: "Average Queue Time" }
      { field: "queueDuration", type: max, name: "Maximum Queue Time" }
      {
        field: "totalDuration"
        type: average
        name: "Average Handle Time by Queue"
      }
    ]
  ) {
    tasks {
      lastQueue {
        name
        id
      }
      aggregation {
        name
        value
      }
    }
    pageInfo {
      hasNextPage
      endCursor
    }
  }
}
